    struct InventoryItem {
        uint32_t item_id;
        uint8_t item_type;
        uint8_t upgrade_level;
        uint8_t infusion;
        uint32_t amount;
        uint32_t durability;
        uint32_t order;
        uint32_t idx;
        // NOTE how to pass in reference only reference?
        BaseItem base_item;
    };

    struct AttunementSlot {
        uint8_t item_id;
        uint8_t remaining_uses;
    };

    struct DSRCharacterInfo {
        int index;
        std::string name;
        uint32_t humanity;
        uint32_t souls;
        uint32_t earned_souls;
        uint32_t hollow_state;
        uint8_t covenant_id;
        uint8_t covenant_levels[10];

        std::vector<uint8_t> used_gestures;

        uint32_t hp_current;
        uint32_t hp_max;
        uint32_t hp_base;
        uint32_t stamina_current;
        uint32_t stamina_max;
        uint32_t stamina_base;

        uint32_t level;
        uint32_t vitality;
        uint32_t attunement;
        uint32_t endurance;
        uint32_t strength;
        uint32_t dexterity;
        uint32_t resistance;
        uint32_t intelligence;
        uint32_t faith;

        uint8_t sex;
        uint8_t class_id;
        uint8_t physique_id;
        uint8_t gift_id;

        uint8_t toxic_res;
        uint8_t bleed_res;
        uint8_t poison_res;
        uint8_t curse_res;

        uint8_t face_id;
        uint8_t hair_style_id;
        uint8_t hair_color_id;

        uint8_t l_hand_slot_1;
        uint8_t l_hand_slot_2;
        uint8_t r_hand_slot_1;
        uint8_t r_hand_slot_2;
        uint8_t l_arrows_slot;
        uint8_t l_bolts_slot;
        uint8_t r_arrows_slot;
        uint8_t r_bolts_slot;
        uint8_t head_slot;
        uint8_t body_slot;
        uint8_t arms_slot;
        uint8_t legs_slot;
        uint8_t l_ring_slot;
        uint8_t r_ring_slot;
        uint8_t q1_slot;
        uint8_t q2_slot;
        uint8_t q3_slot;
        uint8_t q4_slot;
        uint8_t q5_slot;
        std::vector<InventoryItem> inventory_items;
        std::vector<InventoryItem> botomless_box_items;
        std::vector<AttunementSlot> attunement_slots;
    };

    struct DSRSaveFile {
        std::vector<DSRCharacterInfo> characters;
        BND4Entry side_car_enty;
    };

    DSRSaveFile parse_dsr_file(const SL2File& sl2);
